mapper.select:
SELECT base_table.id, base_table.name, base_table.introduction, base_table.district_id, base_table.district_name, base_table.city_id, base_table.city_name, base_table.province_id, base_table.province_name, base_table.address_detail, base_table.latitude, base_table.longitude, base_table.phone, base_table.is_release, IFNULL(total_count.tc,0) total_room_count, IFNULL(total_count.tc,0) - IFNULL(occupied_count.oc,0) free_room_count FROM (SELECT id, name, introduction, district_id, district_name, city_id, city_name, province_id, province_name, address_detail, latitude, longitude, phone, is_release FROM apartment_info
is_deleted = 0
AND province_id = #{queryVo.provinceId}
AND city_id = #{queryVo.cityId}
AND district_id = #{queryVo.districtId}
) base_table LEFT JOIN (SELECT apartment_id, count(*) tc FROM room_info WHERE is_deleted = 0 AND is_release = 1 GROUP BY apartment_id) total_count ON base_table.id = total_count.apartment_id LEFT JOIN (SELECT apartment_id, count(*) oc FROM lease_agreement WHERE is_deleted = 0 AND status in (2, 5, 7) GROUP BY apartment_id) occupied_count ON base_table.id = occupied_count.apartment_id
